$(document).ready(function(){
    $("#middle_up_signin").click(function(){
        $("#middle_body_signin").show("slow");
        $("#middle_body_update").hide("slow");
    });
    $("#middle_up_update").click(function(){
        $("#middle_body_signin").hide("slow");
        $("#middle_body_update").show("slow");
    });
    
    $("#middle_up_signin").click(function(){
        $("#middle_body_signin").show("slow");
        $("#middle_body_signup").hide("slow");
    });
    $("#middle_up_signup").click(function(){
        $("#middle_body_signin").hide("slow");
        $("#middle_body_signup").show("slow");
    });
    $("#update_account_event").click(function(){
        $(".account_info").slideToggle("slow");
        $("#update_account").slideToggle("slow");
    });
});